class TicketsTypes extends BaseTicketsTypes
{
  public static function getJsList()
  {
    $tickets_typess = Doctrine_Core::getTable('TicketsTypes')
      ->createQuery('a')
      ->orderBy('sort_order, name')
      ->execute();
      
    $html = '
      <script>
        var tickets_types_list = new Array();
        var tickets_types_by_sort_order = new Array();
        var default_tickets_types_id = 0;
    ';  
    
    foreach($tickets_typess as $v)
    {
      $html .= 'tickets_types_list[' . $v->getId() .']="' . addslashes($v->getName()) . '";' . "\n";
      
      if($v->getDefaultValue()==1)
      {
        $html .= 'default_tickets_types_id=' . $v->getId() . ";\n";
      }      
    }
    
    $html .= "\n\n";
    
    $i=0;
    foreach($tickets_typess as $v)
    {
      $html .= 'tickets_types_by_sort_order[' . $i .']="' . $v->getId() . '";' . "\n";
      $i++; 
    }
  
    $html .= '</script>';
    
    return $html;
  }
}